








WITH staging AS (
-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    id,
    name

    FROM "postgres"."dbt"."source_country"
),

derived_columns AS (

    SELECT

    id,
    name,
    id AS COUNTRY_KEY,
    'CSV_COUNTRY'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    id,
    name,
    COUNTRY_KEY,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(id AS VARCHAR))), '')), 'hex') AS COUNTRY_PK,

    DECODE(MD5(COALESCE(NULLIF(UPPER(TRIM(CAST(name AS VARCHAR))), ''), '^^')), 'hex') AS COUNTRY_HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    id,
    name,
    COUNTRY_KEY,
    RECORD_SOURCE,
    COUNTRY_PK,
    COUNTRY_HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

SELECT *,
    current_timestamp AS LOAD_DATE,
    current_timestamp AS EFFECTIVE_FROM
FROM staging