








WITH staging AS (
-- Generated by AutomateDV (formerly known as dbtvault)

    

WITH source_data AS (

    SELECT

    id,
    currency_code,
    relation_to_rur,
    date_start,
    date_end

    FROM "postgres"."dbt"."source_currency_rate"
),

derived_columns AS (

    SELECT

    id,
    currency_code,
    relation_to_rur,
    date_start,
    date_end,
    id AS CURRENCY_RATE_KEY,
    'CSV_CURRENCY_RATE'::TEXT AS RECORD_SOURCE

    FROM source_data
),

hashed_columns AS (

    SELECT

    id,
    currency_code,
    relation_to_rur,
    date_start,
    date_end,
    CURRENCY_RATE_KEY,
    RECORD_SOURCE,

    DECODE(MD5(NULLIF(UPPER(TRIM(CAST(id AS VARCHAR))), '')), 'hex') AS CURRENCY_RATE_PK,

    DECODE(MD5(CONCAT(
        COALESCE(NULLIF(UPPER(TRIM(CAST(date_end AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(date_start AS VARCHAR))), ''), '^^'), '||',
        COALESCE(NULLIF(UPPER(TRIM(CAST(relation_to_rur AS VARCHAR))), ''), '^^')
    )), 'hex') AS CURRENCY_RATE_HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    id,
    currency_code,
    relation_to_rur,
    date_start,
    date_end,
    CURRENCY_RATE_KEY,
    RECORD_SOURCE,
    CURRENCY_RATE_PK,
    CURRENCY_RATE_HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

SELECT *,
    current_timestamp AS LOAD_DATE,
    current_timestamp AS EFFECTIVE_FROM
FROM staging