    )), 'hex') AS WAREHOUSE_HASHDIFF

    FROM derived_columns
),

columns_to_select AS (

    SELECT

    id,
    name,
    system_name,
    WAREHOUSE_KEY,
    RECORD_SOURCE,
    WAREHOUSE_PK,
    WAREHOUSE_HASHDIFF

    FROM hashed_columns
)

SELECT * FROM columns_to_select
)

